con:config.script:
import groovy.json.JsonSlurper

def strHost = context.expand( '${#Project#EOSHost}' )
def strUsername = context.expand( '${#Project#UserName}' )
def strPassword = context.expand( '${#Project#Password}' )
def  intPort = context.expand( '${#Project#Port}' )
def CPSLogStr
def env = testRunner.testCase.testSuite.project.getActiveEnvironmentName().toLowerCase()
env = env.substring(2)
def strFileDirectory = '/eos/' + env + '/pin/i1/logs/pin'
def strFileName = "cpseventlog.log"

def strLogRef = context.expand( '${Properties#LogRef}' )

if (!(ServerConnect.Connect(strHost ,strUsername, strPassword, Integer.parseInt(intPort)) == "PASS"))
	assert false," Failed to conenct to linux server!!"
else
 log.info "Connected to the server successfully!!"


def command = "cd $strFileDirectory;grep '$strLogRef' $strFileName"
def CPSLogList = ServerConnect.execCommand(command)

ServerConnect.closeConnection()

if (CPSLogList == "")
	assert false , "CPS Event log not added"
else
	CPSLogStr = CPSLogList.split('INFO CPSEVENTLOG -')[1]

log.info "CPSLogStr-->" + CPSLogStr

testRunner.testCase.getTestStepByName("Properties").setPropertyValue("CPSEventLog", CPSLogStr)

import groovy.json.JsonSlurper

def req_LogCode = context.expand( '${Properties#logCode}' )
def req_BankId = context.expand( '${Properties#orgid}' )
def req_Message = context.expand( '${Properties#message}' )
def req_Severity = context.expand( '${Properties#severity}' )
def req_Method = context.expand( '${Properties#method}' )
def req_Module = context.expand( '${Properties#module}' )
def req_UserId = context.expand( '${Properties#UserID}' )
def req_LogRef = context.expand( '${Properties#LogRef}' )
def req_TppId = context.expand( '${Properties#tppId}' )
def req_TppName = context.expand( '${Properties#tppName}' )

def Res_CPSEventLog = context.expand( '${Properties#CPSEventLog}' )

def slurperResponse = new JsonSlurper().parseText(Res_CPSEventLog)
log.info  slurperResponse.logCode

def res_LogCode = slurperResponse.logCode
def res_BankId = slurperResponse.bankId
def res_Message = slurperResponse.message
def res_Severity = slurperResponse.severity
def res_Method = slurperResponse.method
def res_Module = slurperResponse.module
def res_UserId = slurperResponse.userId
def res_LogRef = slurperResponse.logref
def res_TppId = slurperResponse.tppId
def res_TppName = slurperResponse.tppName

assert req_LogCode.trim() == res_LogCode.toString().trim(), 'Expected logCode : ' + req_LogCode + ' Actual logCode:' + res_LogCode
assert req_BankId.trim() == res_BankId.toString().trim(), 'Expected BankId : ' + req_BankId + ' Actual BankId:' + res_BankId
assert req_Severity.trim() == res_Severity.toString().trim(), 'Expected Severity : ' + req_Severity + ' Actual Severity:' + res_Severity
assert req_Method.trim().toUpperCase() == res_Method.toString().trim().toUpperCase(), 'Expected Method : ' + req_Method + ' Actual Method:' + res_Method
assert req_Module.trim() == res_Module.toString().trim(), 'Expected Module : ' + req_Module + ' Actual Module:' + res_Module
assert req_UserId.trim() == res_UserId.toString().trim(), 'Expected UserId : ' + req_UserId + ' Actual UserId:' + res_UserId
assert req_LogRef.trim() == res_LogRef.toString().trim(), 'Expected LogRef : ' + req_LogRef + ' Actual LogRef:' + res_LogRef
assert req_TppId.trim() == res_TppId.toString().trim(), 'Expected TppId : ' + req_TppId + ' Actual TppId:' + res_TppId
assert req_TppName.trim() == res_TppName.toString().trim(), 'Expected TppName : ' + req_TppName + ' Actual TppName:' + res_TppName
assert req_Message.trim() == res_Message.toString().trim(), 'Expected Message :' + req_Message + ' Actual Message:' + res_Message
