import com.jcraft.jsch.*
import org.apache.commons.io.FileUtils

// Properties
def  eosHost = context.expand( '${#Project#EOSHost}' )
def  Port =  context.expand( '${#Project#Port}' );
def  ftpUsername = context.expand( '${#Project#UserName}' )
def  ftpPassword = context.expand( '${#Project#Password}' )
def env = testRunner.testCase.testSuite.project.getActiveEnvironmentName().toLowerCase()
env=env.substring(2)

def  SourcePath = "/eos/$env/pin/i1/logs/pin"

def logRef = context.expand( '${Create_Future_PM#RawRequest#declare namespace wsc=\'http://edb.com/ws/WSCommon_v21\'; //wsc:AutHeader[1]/wsc:ClientContext[1]/wsc:item[2]/@value}' )

if (!(ServerConnect.Connect(eosHost ,ftpUsername, ftpPassword, Integer.parseInt(Port)) == "PASS"))
	assert false,"Failed to conenct to linux server!!"
else
	assert true

String commandToExecute = "cd $SourcePath;  grep  -A 6 $logRef  jaxrspayload.log"
def CommandOutput = ServerConnect.execCommand(commandToExecute)

if (!(ServerConnect.closeConnection()))
	assert false," Failed to close linux server!!"
else
 	assert true

def splitOutput = CommandOutput.split('\\n\\n')
def requestTemp = splitOutput[0].split('Payload: ')[1]
def responseTemp =  splitOutput[1].split('Payload: ')[1]

log.info requestTemp
log.info responseTemp

testRunner.testCase.testSteps['Properties_PM_4201'].setPropertyValue('PFCRequestLog',requestTemp)
testRunner.testCase.testSteps['Properties_PM_4201'].setPropertyValue('PFCResponseLog',responseTemp)

import org.apache.commons.io.FileUtils

// PFC Request Validation
def FeatureName = context.expand( '${Properties_PM_4201#PFCRequestLog#$[\'Verify\'][0]}' )
def CountryCode = context.expand( '${Properties_PM_4201#PFCRequestLog#$[\'CountryCode\']}' )
def TransferCurrency = context.expand( '${Properties_PM_4201#PFCRequestLog#$[\'Amount\'][\'Currency\']}' )
def ImmediateTransfer = context.expand( '${Properties_PM_4201#PFCRequestLog#$[\'Immediate\']}' )
def PaymentType = context.expand( '${Properties_PM_4201#PFCRequestLog#$[\'Domestic\']}' )
def CustId = context.expand( '${Properties_PM_4201#PFCRequestLog#$[\'AccountOwner\']}' )
def MarketType = context.expand( '${Properties_PM_4201#PFCRequestLog#$[\'Market\']}' )

def CredAgentCountry = context.expand( '${Properties_PM_4201#CreditorCountry}' )
def customerid = context.expand( '${Properties_PM_4201#customerid}' )


assert FeatureName == "VERIFY_CUSTOMERLIMITS_COUNTRY","feature name not valid"
assert CountryCode == CredAgentCountry,"CountryCode not valid"
assert TransferCurrency == "NOK","TransferCurrency not valid"
assert ImmediateTransfer == "NON_IMMEDIATE","ImmediateTransfer not valid"
assert PaymentType == "CROSSBORDER","PaymentType is not valid"
//assert CustId == customerid,"CustId not valid"
assert MarketType == "BM","MarketType is not valid"


// ==========================================

// PFC Response Validation
def ResponseCode = context.expand( '${Properties_PM_4201#PFCResponseLog#$[\'Code\']}' )
def Description = context.expand( '${Properties_PM_4201#PFCResponseLog#$[\'Description\']}' )

assert ResponseCode == "0","ResponseCode is not valid"
assert Description == "OK","Description is not valid"

