con:config.script:
import groovy.sql.Sql;

connectPwh("true");

public void connectPwh( status )
{
	identifyTestCase();
	boolean details = fetchDetails();
	def resultSet;
	def currency = "NOK";
	if(sepa) {
		currency = "EUR"
	}

	query1 = " SELECT USERID, ACCOUNTNUMBER,CURRENCYCODE FROM V_AUTH_USERINFO WHERE LENGTH(USERID) = '11' AND ACCOUNTROLENAME = 'Register' "
	query2 = " AND MARKETTYPE = 'PM' AND BANKORGID = "+orgid+" AND CURRENCYCODE = '"+currency+"' AND ACCOUNTNUMBER = '42020261298' "
	query3 = " AND ROWNUM = 1"
	sqlQuery = query1+query2+query3;
//96500125915 - 42020261298 - 24804665230 - 36250103381
	if (status == 'true' && details == true)
	{
		connection	=	Sql.newInstance(connect, "pwhdata", "pwh", "oracle.jdbc.driver.OracleDriver");
		resultSet		=	connection.rows(sqlQuery)
		connection.close();

		if (resultSet != "" && resultSet.USERID[0] != null && resultSet.ACCOUNTNUMBER[0] != null)
		{
			def customers=	resultSet.USERID[0].toString();
			def account	= 	resultSet.ACCOUNTNUMBER[0].toString();
			currency		= 	resultSet.CURRENCYCODE[0].toString();
			def userid		= 	customers;

			if (employee)
			{
				employee_id = "J408Z98";
				testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Employee",employee_id);
				userid = employee_id;
			}
			testRunner.testCase.getTestStepByName("Properties").setPropertyValue("customerid",customers);
			testRunner.testCase.getTestStepByName("Properties").setPropertyValue("userid",userid);
			testRunner.testCase.getTestStepByName("Properties").setPropertyValue("debitAccount",account);
			testRunner.testCase.getTestStepByName("Properties").setPropertyValue("DebitAcntccy",currency);
			log.info "Test Data setting Done.! -->" +resultSet
		}
		else {
			log.info "Result Set Empty --> "+resultSet
		}
	}
	else log.info "Unable to proceed further due to one value is FALSE ( \"connect pwh: \""+status+"   ;   \"details fetched: \""+details+" ) ";
}

public void identifyTestCase()
{
	testCaseName	=	context.getCurrentStep().getTestCase().getLabel();
	testCaseName	= 	testCaseName.toLowerCase();
	marketType		=	"PM";
	payChannel		=	"INT"
	customertype		=	'P';
	sepa 				= 	false;
	employee			=	false;

	if (testCaseName.contains("sepa"))
	{
		sepa = true;
	}

	if (testCaseName.contains("bm"))
	{
		marketType = "BM"
		payChannel = "BES";
		customertype = 'N';
	}
	else if (testCaseName.contains("pm"))
	{
		marketType = "PM"
		payChannel = "INT";
		customertype = 'P';
	}

	if (testCaseName.contains("emp"))
	{
		employee = true;
		payChannel = "ITR";
	}
	testRunner.testCase.getTestStepByName("Properties").setPropertyValue("PaymentChannel",payChannel);
}

public boolean fetchDetails()
{
	orgid = context.expand( '${Properties#orgid}' );

	envDetails		=	DetectTestEnvironment.getDetailsOf(context.testCase.testSuite.project.getActiveEnvironment().getName());
	orgidDetails	=	DetectTestEnvironment.getBankDetailsOf(orgid);

	if (envDetails[3] != "" && orgidDetails[2] != "" && orgidDetails[3] != "") {
		connect		=	envDetails[3];
		credentials	=	orgidDetails[2];
		debtorAgent	=	orgidDetails[3];

		testRunner.testCase.getTestStepByName("Properties").setPropertyValue("credentials",credentials);
		testRunner.testCase.getTestStepByName("Properties").setPropertyValue("DebtorAgent_Bic",debtorAgent);
		return true;
	}
	return false;
}

