con:config.script:
setData ("COMPLETED","TRANSACTIONSID","TestData");
setData ("COMPLETED","ENDTOENDID","TestData");
setData ("COMPLETED","PAYMENT_REF","TestData");

setData ("REJECTED","TRANSACTIONSID","TestData");
setData ("REJECTED","ENDTOENDID","TestData");
setData ("REJECTED","PAYMENT_REF","TestData");

setData ("PROCESSING","TRANSACTIONSID","TestData");
setData ("PROCESSING","ENDTOENDID","TestData");
setData ("PROCESSING","PAYMENT_REF","TestData");

public setData (jdbcStep,row,testData){
	def setValue = context.expand( '${'+jdbcStep+'#ResponseAsXml#//Results[1]/ResultSet[1]/Row[1]/'+row+'[1]}' );
	context.testCase.getTestStepByName(testData).setPropertyValue(jdbcStep+"_"+row,setValue);
}
