	log.info statusreason;
	testRunner.testCase.getTestStepByName("Properties").setPropertyValue("grpActualStatus",Grpstatus[0]);
	testRunner.testCase.getTestStepByName("Properties").setPropertyValue("actualStatus",status[0]);
	testRunner.testCase.getTestStepByName("Properties").setPropertyValue("actualStatusReason",statusreason[0]);
