con:config.script:
connectToServer();
public void connectToServer(){
	def strHost = context.expand( '${#Project#EBSHost}');
	def  intPort = context.expand( '${#Project#Port}');
	def strUsername = context.expand( '${#Project#UserName}');
	def strPassword = context.expand( '${#Project#Password}');
	def date =  java.time.LocalDateTime.now();
	def jobRunType = context.expand( '${Properties#jobRunType}').toUpperCase();
	def processingLine = context.expand( '${Properties#processingLine}').toUpperCase();
	def paymentStatus = context.expand( '${Properties#paymentStatus}').toUpperCase()
	def bankID = context.expand( '${Properties#bankID}');
	def env = testRunner.testCase.testSuite.project.getActiveEnvironmentName().toLowerCase().toString().substring(2);

	if (!(ServerConnect.Connect(strHost ,strUsername, strPassword, Integer.parseInt(intPort)) == "PASS")) { assert false," Failed to conenct to linux server!!";} else
	{	log.info "Connected to the server successfully!!  Running the batch.... : "+env+"__"+date;
		def nextDate = date.format(java.time.format.DateTimeFormatter.ofPattern('yyyy/MM/dd'));
		def runId = date.format(java.time.format.DateTimeFormatter.ofPattern("YYYY-MM-dd'T'HH:mm:ss.SSSSSSSSS"));
		String shParameter = "lowVolumeReservationLauncher.sh processLine=$processingLine  paymentStatuses=$paymentStatus releaseDate=${nextDate} jobRunType=$jobRunType bankIds=$bankID runId=$runId";
		String CommandOutPut = ServerConnect.execBatchCommand(env,"pin",shParameter);
		log.info "shParameter : "+shParameter;
		log.info "CommandOutput returned-->" + CommandOutPut;
		ServerConnect.closeConnection();
		def porSuccessMessage1 = 'LOW_VOLUME_RESERVATION_JOB completed, nothing to do';
		def porSuccessMessage2 ='LOW_VOLUME_RESERVATION_JOB went OK';
		assert (CommandOutPut.trim() != "" && (CommandOutPut.contains(porSuccessMessage1) ||  CommandOutPut.contains(porSuccessMessage2))), "CommandLine OutPut having error!!, Executed command :" + CommandOutPut;
		String POR1LastModifiedTime = System.currentTimeMillis().toString().substring(0,10);
		testRunner.testCase.getTestStepByName("Properties").setPropertyValue("POR1LastModifiedTime","$POR1LastModifiedTime");
	}
}
