def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context ) //get the context variable def OperationArray = [] //Array list def XMLContainer = groovyUtils.getXmlHolder(context.getCurrentStep().getLabel() + "#Response")
OperationArrayViewPayment = XMLContainer.getNodeValues("//*:operationsArray") assert OperationArrayViewPayment.contains("VIEW_PAYMENT")
OperationArrayDeletePayment = XMLContainer.getNodeValues("//*:operationsArray") assert OperationArrayDeletePayment.contains("DELETE_PAYMENT") OperationArrayApprovePayment = XMLContainer.getNodeValues("//*:operationsArray") assert !OperationArrayApprovePayment.contains("APPROVE_PAYMENT")

