OperationArrayViewPayment = XMLContainer.getNodeValues("//*:operationsArray") assert OperationArrayViewPayment.contains("VIEW_PAYMENT") assert !OperationArrayViewPayment.contains("UPDATE_PAYMENT") assert !OperationArrayViewPayment.contains("CONFIRM_PAYMENT") assert OperationArrayViewPayment.contains("PRIORITIZE_PAYMENT") assert OperationArrayViewPayment.contains("STOP_PAYMENT")
assert OperationArrayViewPayment.contains("DELETE_PAYMENT") assert !OperationArrayViewPayment.contains("HIDE_PAYMENT") assert !OperationArrayViewPayment.contains("APPROVE_PAYMENT") assert OperationArrayViewPayment.contains("UNAPPROVE_PAYMENT")

import com.eviware.soapui.support.XmlHolder
import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.Calendar;
import java.util.Date;
import java.util.TimeZone;

def path=context.expand( '${projectDir}')
