con:config.script:
import groovy.io.FileType
import java.text.SimpleDateFormat;
import java.util.Calendar;
import java.util.Date;

def dir = new File(context.getTestCase().getTestSuite().getProject().getPath()+'\\Testdata')
def painfile=[]
dir.eachFile (FileType.FILES) { file ->  
painfile.add(file.text)
log.info painfile
}

testRunner.testCase.getTestStepByName("Properties").setPropertyValue("MessageToSend",painfile.toString());

Date date = new Date();
SimpleDateFormat fn = new SimpleDateFormat("ddMyyhhmmssSSS");
def datetime = fn.format(date)

testRunner.testCase.getTestStepByName("Properties").setPropertyValue("MsgID",datetime );

def preparedPainFile=[]
for (item in painfile)
 	{
item = item.replaceAll("TIMESTAMP",datetime) ;
item = item.replaceAll("CUSTOMERID",context.expand( '${Properties#customerid}' )) ;
item = item.replaceAll("ACCOUNTID",context.expand( '${Properties#AccountID}' )) ;
item = item.replaceAll("DATE",context.expand( '${Properties#DueDate}' )) ;
//Positive greater than 24 char credit acc
item = item.replaceAll("ACCOUNTC",context.expand( '${Properties#CaccountID}' )) ;
preparedPainFile.add(item)
 	}

testRunner.testCase.getTestStepByName("Properties").setPropertyValue("messagetoSend_Updated",preparedPainFile.toString());

import MQMessageUtils;
// Create utility class object, Create MQ connection
MQMessageUtils mqMessageUtils = new MQMessageUtils(context.expand( '${#Project#mqManager}' ),
context.expand( '${#Project#mqHostname}' ),context.expand( '${#Project#mqPort}' ) as Integer,
context.expand( '${#Project#mqChannelAuth}' ),log);

for (finalpainmessage in preparedPainFile)

 	log.info ("Message Sent :" + mqMessageUtils.sendMessage(context.expand( '${#Project#sendQueue}'),finalpainmessage));
 sleep(8000)

