SELECT M.MESSAGEID,P.PAYMENTINFOID,T.TRANSACTIONSID AS TRANSID,T.PAYMENTTYPE,T.PAYMENTSTATUS AS STATUS,T.PROCESSINGBANKORGID,P.DEBETACCOUNTNO,T.DEBIT_BOOKING_ID,M.MESSAGEIDENTIFICATION AS MSGID,T.INSTRUCTEDAMOUNT AS AMT,T.RELEASEDATE,T.FK_PROCESSINGLINEID AS PROCLINE,T.FIRSTLACKOFFUNDSMESSAGESENT AS LAFU_MSG,T.INITIATORBANKID,M.CREATIONDATETIME,T.STATUSCHANGETIME,M.MARKETTYPE,M.AGREEMENTID,M.INITIATORID,M.CUSTOMERID,A.APPROVERIDENTIFIER AS APPROVER,
T.ENDTOENDID,  M.ORIGINALCHANNEL,AC.OTHR_IDENTFCTION_TYPE AS CREDITOR,AC.OTHER_ID AS CREDITOR_BBAN,AC.IBAN AS CREDITOR_IBAN, T.AGREEMENTID AS SO_AGRMNT,P.SERVICELEVEL,M.PAINVERSION AS PAIN,P.PMTINFOID,T.INSTRUCTIONID,M.ORIGINALFORMAT,T.ISPC,P.CATEGORYPURPOSECODE AS CATGRYPURPS,T.RECEIPTORDER,T.SEPA,T.WB_FLAG,T.PRIORITIZED,T.RISKSTATUS,T.FORCE_FUND,
  T.REQUESTEDEXECUTIONDATE,L.LASTOFDAY_COUNTER,I.INSTRUCTIONTYPE,I.ADDITIONALINFO,I.INSTRUCTIONFUNCTION,R.RESERVATION_TYPE,R.RESERVATIONREFERENCE,SR.CODE ,SRA.ADDITIONALINFORMATION AS REJECT_REASON, AT.BICFI AS CREDITOR_AGENT_BIC, AT.NAME AS BANK_NAME
FROM HST_TRANSACTIONS T INNER JOIN HST_PAYMENTINFO P ON T.FK_PAYMENTINFOID=P.PAYMENTINFOID INNER JOIN HST_MESSAGE M ON M.MESSAGEID=P.FK_MESSAGEID LEFT OUTER JOIN LACKOFFUNDSTRANSACTIONS L ON L.FK_TRANSACTIONSID=T.TRANSACTIONSID LEFT OUTER JOIN HST_INSTRUCTIONS I ON I.FK_TRANSACTIONSID=T.TRANSACTIONSID LEFT OUTER JOIN HST_APPROVER A ON A.FK_TRANSACTIONSID=T.TRANSACTIONSID LEFT OUTER JOIN RESERVATION_DETAILS R 
  ON R.FK_TRANSACTIONSID=T.TRANSACTIONSID LEFT OUTER JOIN STATUSREASON SR ON SR.FK_TRANSACTIONID=T.TRANSACTIONSID LEFT OUTER JOIN STATUSREASON_ADDT_INF SRA ON SRA.FK_STATUSREASONID=SR.STATUSREASONID LEFT OUTER JOIN HST_ACCOUNT_CREDITOR AC ON AC.FK_TRANSACTIONSID=T.TRANSACTIONSID  LEFT OUTER JOIN HST_AGENT_TRANS AT ON AT.FK_TRANSACTIONSID=T.TRANSACTIONSID WHERE 1=1 
--AND T.INITIATORBANKID IN ('3625') --AND T.DEBETACCOUNTNO IN ('42023281230') AND T.TRANSACTIONSID IN ('261097149','261098978')
--AND T.PAYMENT_REF='402845'  --AND M.MESSAGEIDENTIFICATION LIKE 'B2B%' --  AND TRUNC(M.CREATIONDATETIME) >= '26-JUL-20' --AND M.CREATIONDATETIME > SYSDATE-(20/24/60) --TRUNC(SYSDATE-1) 
--AND T.FK_PROCESSINGLINEID IN ('57') --AND T.ENDTOENDID LIKE '%M_%' --AND T.PAYMENTTYPE IN ('FDMS','FDOM') --AND T.STATUSCHANGETIME > SYSDATE-(1) 
ORDER BY T.STATUSCHANGETIME DESC,T.TRANSACTIONSID DESC;
