def fullBalanceAvailable = context.expand( '${Properties#fullBalanceAvailable}' )

def  balanceLimit = (Double.parseDouble(fullBalanceAvailable) + 1)
balanceLimit = balanceLimit.trunc()

testRunner.testCase.testSuite.setPropertyValue("BalanceLimitAmount",balanceLimit.toString())

def balanceLimitAmount = context.expand( '${Properties#BalanceLimitAmount}' )

def TriggerAmount = (Double.parseDouble(balanceLimitAmount)+1)
TriggerAmount = TriggerAmount.trunc()
testRunner.testCase.testSuite.setPropertyValue("TriggerAmount",TriggerAmount.toString())

def availableBalance = context.expand( '${Properties#AvailableBalance}' )
if (availableBalance.contains("-"))
{
	def NegativeBalanceCustomer = context.expand( '${#TestSuite#customerid}' )
	def NegativeBalanceCustomer1 = context.expand( '${#TestSuite#NegativeBalanceCustomer}' )
	if (NegativeBalanceCustomer1 == null) NegativeBalanceCustomer1 = NegativeBalanceCustomer
	testRunner.testCase.testSuite.setPropertyValue("NegativeBalanceCustomer",  NegativeBalanceCustomer1+ ',' +"'" + NegativeBalanceCustomer +"'")
	testRunner.runTestStepByName("FetchAccount")
	testRunner.runTestStepByName("SetAccount")
	testRunner.runTestStepByName("BALANCE_CHECK")
	testRunner.runTestStepByName("BALANCE_CHECK_Credit")
	testRunner.runTestStepByName("balanceTransfer")
	testRunner.runTestStepByName("SetBalanceLimitAmount")
	testRunner.runTestStepByName("BalanceNegativeCheck")
}
else
{
	testRunner.runTestStepByName("STOAGREEMENT")
	testRunner.runTestStepByName("IF ACTIVE")
	testRunner.runTestStepByName("SO Close")
}

