import groovy.json.JsonSlurper

def strHost = context.expand( '${#Project#EBSHost}' )
def strUsername = context.expand( '${#Project#UserName}' )
def strPassword = context.expand( '${#Project#Password}' )
def  intPort = context.expand( '${#Project#Port}' )

testRunner.testCase.getTestStepByName("Properties").setPropertyValue("iHubResponseLog", "")
testRunner.testCase.getTestStepByName("Properties").setPropertyValue("iHubRequestLog", "")

def env = testRunner.testCase.testSuite.project.getActiveEnvironmentName().toLowerCase()
env = env.toString().substring(2)
def strFileDirectory = '/ebs/' + env + '/pin/logs/pin/iHubDeleteAgreement'

//def strLogRef = context.expand( '${Properties#LogRef}' )
def soAgreementId = context.expand( '${Properties#soAgreementId}' )
def soAgreementVersion = context.expand( '${Properties#soAgreementVersion}' )
def centralAgreementId = context.expand( '${Properties#centralAgreementId}' )
strLogRef = "reference>" + soAgreementId + "-" + soAgreementVersion + "</"


if (!(ServerConnect.Connect(strHost ,strUsername, strPassword, Integer.parseInt(intPort)) == "PASS"))
	assert false," Failed to conenct to linux server!!"

//Fetch IHub Request xml
grepString = "grep -i -E 'agreementDeleteRequest.*${centralAgreementId}.*agreementDeleteRequest' wspayload.log"
grepString = 'cd ' + strFileDirectory + ';' + grepString

def iHubRequestLog = ServerConnect.execCommand(grepString)
iHubRequestLog = iHubRequestLog.replace('Payload: ','')


//Fetch IHub Response xml
grepString=""
grepString = "grep -i -E 'agreementDeleteResponse.*${centralAgreementId}.*agreementDeleteResponse' 'wspayload.log'"
grepString = 'cd ' + strFileDirectory + ';' + grepString

def iHubResponseLog = ServerConnect.execCommand(grepString)
iHubResponseLog = iHubResponseLog.replace('Payload: ','')

ServerConnect.closeConnection()

assert iHubRequestLog.trim() != "", "iHubRequestLog log not found in iHubRequestLog.log!!, Grep Command:" + grepString
assert iHubResponseLog.trim() != "", "iHubResponseLog log not found in iHubResponseLog.log!!, Grep Command:" + grepString

testRunner.testCase.getTestStepByName("Properties").setPropertyValue("iHubResponseLog", iHubResponseLog.trim())
testRunner.testCase.getTestStepByName("Properties").setPropertyValue("iHubRequestLog", iHubRequestLog.trim())

def reqCentralAgreementId = context.expand( '${Properties#centralAgreementId}' )
def iHubRequestLog = testRunner.testCase.getTestStepByName("Properties").getPropertyValue("iHubRequestLog")

def slurperResponse = new XmlSlurper().parseText(iHubRequestLog)

def resCentralAgreementId = slurperResponse.Body.agreementDeleteRequest.qualification.agreementIntId.toString()


assert reqCentralAgreementId.trim() == resCentralAgreementId.toString().trim(), 'Expected CentralAgreementId : ' + reqCentralAgreementId + ' Actual CentralAgreementId:' + resCentralAgreementId

def resCentralAgreementId = context.expand( '${Properties#centralAgreementId}' )
def iHubResponseLog = testRunner.testCase.getTestStepByName("Properties").getPropertyValue("iHubResponseLog")
def slurperResponse = new XmlSlurper().parseText(iHubResponseLog)

def reqCentralAgreementId = slurperResponse.Body.agreementDeleteResponse.result.agreementIntId.toString()

assert reqCentralAgreementId.trim() == resCentralAgreementId.toString().trim(), 'Expected CentralAgreementId : ' + reqCentralAgreementId + ' Actual CentralAgreementId:' + resCentralAgreementId
