import groovy.json.JsonSlurper

def strHost = context.expand( '${#Project#EOSHost}' )
def strUsername = context.expand( '${#Project#UserName}' )
def strPassword = context.expand( '${#Project#Password}' )
def  intPort = context.expand( '${#Project#Port}' )

testRunner.testCase.getTestStepByName("Properties").setPropertyValue("iHubResponseLog", "")
testRunner.testCase.getTestStepByName("Properties").setPropertyValue("iHubRequestLog", "")

def env = testRunner.testCase.testSuite.project.getActiveEnvironmentName().toLowerCase()
env = env.toString().substring(2)
def strFileDirectory = '/eos/' + env + '/pin/i1/logs/pin'

//def strLogRef = context.expand( '${Properties#LogRef}' )
def soAgreementId = context.expand( '${Properties#soAgreementId}' )
def soAgreementVersion = context.expand( '${Properties#soAgreementVersion}' )
def centralAgreementId = context.expand( '${Properties#centralAgreementId}' )
strLogRef = "reference>" + soAgreementId + "-" + soAgreementVersion + "</"


if (!(ServerConnect.Connect(strHost ,strUsername, strPassword, Integer.parseInt(intPort)) == "PASS"))
	assert false," Failed to conenct to linux server!!"

//Fetch IHub Request xml
grepString = "grep -i -E '${strLogRef}' wspayload.log"
grepString = 'cd ' + strFileDirectory + ';' + grepString

def iHubRequestLog = ServerConnect.execCommand(grepString)
iHubRequestLog = iHubRequestLog.replace('Payload: ','')


//Fetch IHub Response xml
grepString=""
grepString = "grep -i -E 'agreementWithAttributeCreateResponse.*${centralAgreementId}.*agreementWithAttributeCreateResponse' wspayload.log"
grepString = 'cd ' + strFileDirectory + ';' + grepString

def iHubResponseLog = ServerConnect.execCommand(grepString)
iHubResponseLog = iHubResponseLog.replace('Payload: ','')

ServerConnect.closeConnection()

assert iHubRequestLog.trim() != "", "iHubRequestLog log not found in iHubRequestLog.log!!, Grep Command:" + grepString
assert iHubResponseLog.trim() != "", "iHubResponseLog log not found in iHubResponseLog.log!!, Grep Command:" + grepString

testRunner.testCase.getTestStepByName("Properties").setPropertyValue("iHubResponseLog", iHubResponseLog.trim())
testRunner.testCase.getTestStepByName("Properties").setPropertyValue("iHubRequestLog", iHubRequestLog.trim())

def reqCustomerNumber = context.expand( '${Properties#customerid}' )

def reqAmount = context.expand( '${Properties#Amount}' )
def reqFirstTransactionDate = context.expand( '${Properties#firstTransactionDate}' ).toString()
def reqRecurrenceType = context.expand( '${Properties#recurrenceType}' )
def reqCurrencyCode = context.expand( '${Properties#DebitorCurrency}' )
def reqCreditAccountNumber = context.expand( '${Properties#CreditAccountNumber}' )
def reqProductNumber = '1000'
def reqValidFromDate = '${= java.time.LocalDateTime.now().format(java.time.format.DateTimeFormatter.ofPattern('+"'"+'yyyy-MM-dd'+"'"+'))}'
def reqOpeningDate = '${= java.time.LocalDateTime.now().format(java.time.format.DateTimeFormatter.ofPattern('+"'"+'yyyy-MM-dd'+"'"+'))}'
def soAgreementId = context.expand( '${Properties#soAgreementId}' )
def soAgreementVersion = context.expand( '${Properties#soAgreementVersion}' )
def reqReference = soAgreementId + '-' + soAgreementVersion
def reqTypeCode = 'TEKST'
def reqAttributeText = IHubLibrary.validatePeriodicIHubText(reqAmount,reqCurrencyCode,reqCreditAccountNumber,reqFirstTransactionDate,reqRecurrenceType,log)
def initialFunctionId = 'SOPE'
def iHubRequestLog = testRunner.testCase.getTestStepByName("Properties").getPropertyValue("iHubRequestLog")


	def slurperResponse = new XmlSlurper().parseText(iHubRequestLog)


def resCustomerNumber = slurperResponse.Body.agreementWithAttributeCreateRequest.qualification.customerKey.internationalCustomerNumber	
	def resProductNumber = slurperResponse.Body.agreementWithAttributeCreateRequest.qualification.productKey.productKeyAlt1.productNumber	
	def resSequenceNumber = slurperResponse.Body.agreementWithAttributeCreateRequest.qualification.productKey.productKeyAlt1.sequenceNumber	
	def resValidFromDate = slurperResponse.Body.agreementWithAttributeCreateRequest.qualification.details.validFromDate.toString()
	resValidFromDate =  '${= java.time.LocalDateTime.now().format(java.time.format.DateTimeFormatter.ofPattern('+"'"+'yyyy-MM-dd'+"'"+'))}'
	def resOpeningDate = slurperResponse.Body.agreementWithAttributeCreateRequest.qualification.details.openingDate.toString()
	resOpeningDate =  '${= java.time.LocalDateTime.now().format(java.time.format.DateTimeFormatter.ofPattern('+"'"+'yyyy-MM-dd'+"'"+'))}'
	def resAccountNumber = slurperResponse.Body.agreementWithAttributeCreateRequest.qualification.details.accountNumber	
	def resReference = slurperResponse.Body.agreementWithAttributeCreateRequest.qualification.details.reference	
	def resTypeCode = slurperResponse.Body.agreementWithAttributeCreateRequest.qualification.attribute.typeCode	
	def  resAttributeText = slurperResponse.Body.agreementWithAttributeCreateRequest.qualification.attribute.text 


assert reqAttributeText.trim() == resAttributeText.toString().trim(), 'Expected AttributeText : ' + reqAttributeText + ' Actual AttributeText:' + resAttributeText
assert reqCustomerNumber.trim() == resCustomerNumber.toString().trim(), 'Expected CustomerNumber : ' + reqCustomerNumber + ' Actual CustomerNumber:' + resCustomerNumber
assert reqProductNumber.toString().trim() == resProductNumber.toString().trim(), 'Expected ProductNumber : ' + reqProductNumber + ' Actual ProductNumber:' + resProductNumber
assert reqReference.trim() == resReference.toString().trim(), 'Expected Reference : ' + reqReference + ' Actual Reference:' + resReference
assert reqTypeCode.trim() == resTypeCode.toString().trim(), 'Expected TypeCode : ' + reqTypeCode + ' Actual TypeCode:' + resTypeCode
assert reqOpeningDate.trim() == resOpeningDate.toString().trim(), 'Expected OpeningDate : ' + reqOpeningDate + ' Actual OpeningDate:' + resOpeningDate
assert reqValidFromDate.trim() == resValidFromDate.toString().trim(), 'Expected ValidFromDate : ' + reqValidFromDate + ' Actual ValidFromDate:' + resValidFromDate

def resCentralAgreementId = context.expand( '${Properties#centralAgreementId}' )

def iHubResponseLog = testRunner.testCase.getTestStepByName("Properties").getPropertyValue("iHubResponseLog")
def slurperResponse = new XmlSlurper().parseText(iHubResponseLog)
def reqCentralAgreementId = slurperResponse.Body.agreementWithAttributeCreateResponse.result.agreementIntId.toString()

assert reqCentralAgreementId.trim() == resCentralAgreementId.toString().trim(), 'Expected CentralAgreementId : ' + reqCentralAgreementId + ' Actual CentralAgreementId:' + resCentralAgreementId

