import com.eviware.soapui.impl.wsdl.teststeps.JdbcRequestTestStep
	import com.eviware.soapui.impl.wsdl.teststeps.DebuggableWsdlGroovyScriptTestStep
	import com.eviware.soapui.model.testsuite.TestStepResult

	def time =  CommonLibrary.getDate('yyyyMMddHHmmssSSS')
	def date1 = CommonLibrary.getDate('yyyyMMdd')

	def objProject = testRunner.getTestCase().getTestSuite().getProject()
	def projPath = objProject.getPath()

	filePath =projPath + File.separator + "results"
	new File(filePath).mkdir()
	filePathDate =filePath + File.separator +date1 + File.separator
	new File(filePathDate).mkdir()
	def suiteName = testRunner.testCase.testSuite.name.replaceAll("[^a-zA-Z0-9]+","");
	filePathSuite = filePath + File.separator +date1 + File.separator + suiteName + File.separator 
//	filePathCaseName = filePathSuite + testRunner.testCase.name + File.separator
	new File(filePathSuite).mkdir()

//	new File(filePathCaseName).mkdir()
	 fos = new FileOutputStream(filePathSuite + testRunner.testCase.name + "_"+ testStepResult.testStep.label + time + '.txt', true)
	 pw = new PrintWriter(fos)
	 def CurrentStep = testRunner.testCase.getTestStepAt(context.getCurrentStepIndex()).getLabel()
	def testStep =  testRunner.testCase.getTestStepByName(CurrentStep)
	log.info "After step-->"+CurrentStep
	testStepResult.writeTo(pw)
	if ( testStep instanceof JdbcRequestTestStep )
	{
		def lineSeperator =  "\n ****************************************************************************\n " 
		def rawRequest = new String(testStep.testRequest.response.getRawRequestData())
		def res = 	lineSeperator + rawRequest+ lineSeperator +  testStep.testRequest.response.responseContent	
		pw.write(res)
	}
	 pw.close()
	 fos.close()
